import React, { Component } from "react";
// import ReactDOM from "react-dom";
// import Navigation from '../Navigation/Navigation';
import Messages from '../Messages/Messages';
import './Home.css';

const URL = 'ws://localhost:3030';
// WEBSOCKETS - transfers data through transmission control proticol (TCP)

class MyApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: [],
    };

    this.ws = new WebSocket(URL);

    // this.loadResponse = this.loadResponse.bind(this);
    this.submitMessage = this.submitMessage.bind(this);
  }

  componentDidMount() {
    // this.loadResponse();

    this.state.ws.onopen = () => {
      console.log('connected');
    }

    this.state.ws.onmessage = evt => {
      const message = JSON.parse(evt.data);
      this.setState(state => ({ messages : [message, ...state.messages] }));
      console.log('messaging');

    }
    this.state.ws.onclose = () => {
      // automatically try to reconnect after lost connectiong
      this.setState({ ws: new WebSocket(URL) });
    }
  }

  submitMessage(message) {
    // on submitting the ChatInput form, send the message, add it to the list and reset the input
    const msg = { message: message };
    this.ws.send(JSON.stringify(msg));
    this.setState(state => ({ messages : [msg, ...state.messages] }));
